import React from "react";

import {
  RadioGroup,
  FormControl,
  FormLabel,
  Button,
  Radio,
  Sheet,
  Box,
} from "@mui/joy";
function pageOne() {
  return (
    <FormControl>
      <RadioGroup
        aria-labelledby="storage-label"
        defaultValue="512GB"
        size="lg"
        sx={{ gap: 1.5 }}
      >
        {["512GB", "1TB", "2TB"].map((value) => (
          <Sheet
            key={value}
            sx={{
              p: 2,
              borderRadius: "md",
              boxShadow: "sm",
            }}
          >
            <Radio
              label={`${value} SSD storage`}
              overlay
              disableIcon
              value={value}
              slotProps={{
                label: ({ checked }) => ({
                  sx: {
                    fontWeight: "lg",
                    fontSize: "md",
                    color: checked ? "text.primary" : "text.secondary",
                  },
                }),
                action: ({ checked }) => ({
                  sx: (theme) => ({
                    ...(checked && {
                      "--variant-borderWidth": "2px",
                      "&&": {
                        // && to increase the specificity to win the base :hover styles
                        borderColor: "primary"
                      },
                    }),
                  }),
                }),
              }}
            />
          </Sheet>
        ))}
      </RadioGroup>
    </FormControl>
  );
}

export default pageOne;
