import React from "react";

import {
  RadioGroup,
  FormControl,
  FormLabel,
  Button,
  Radio,
  Sheet,
  Box,
} from "@mui/joy";

function pageOne({ setQuizStep, quizStep }) {
  return (
    <FormControl sx={{ width: "40rem" }}>
      <div className="flex flex-col">
        <div aria-label="goal-question">
          <FormLabel sx={{ fontSize: "1.3em" }}>What are your goals?</FormLabel>
          <RadioGroup
            aria-labelledby="goal-label"
            defaultValue="Lose Weight"
            size="lg"
          >
            <div className="flex justify-between text-center">
              {["Lose Weight", "Maintain Weight", "Gain Weight"].map(
                (value) => (
                  <Sheet
                    key={value}
                    sx={{
                      p: 2,
                      borderRadius: "md",
                      boxShadow: "sm",
                      width: "30%",
                    }}
                  >
                    <Radio
                      label={`${value}`}
                      overlay
                      disableIcon
                      value={value}
                      onClick={() => {
                        localStorage.setItem("goal", value);
                      }}
                    />
                  </Sheet>
                )
              )}
            </div>
          </RadioGroup>
          <div className="w-full flex justify-between">
            <Button
              variant="soft"
              onClick={() => {
                setQuizStep(null);
              }}
            >
              Previous
            </Button>
            <Button
              variant="soft"
              aria-label=""
              onClick={() => {
                setQuizStep(quizStep + 1);
              }}
            >
              Next
            </Button>
          </div>
        </div>
      </div>
    </FormControl>
  );
}

export default pageOne;
