import React from "react";

import { useState, useEffect } from "react";
import {
  RadioGroup,
  FormControl,
  FormLabel,
  Button,
  Radio,
  Sheet,
  Input,
  Box,
  Typography,
  FormHelperText,
} from "@mui/joy";

function Form({ setQuizStatus }) {
  const [values, setValues] = useState({
    byte_name: localStorage.getItem("byte_name"),
    byte_age: localStorage.getItem("byte_age"),
    byte_height_feet: localStorage.getItem("byte_height_feet"),
    byte_height_inches: localStorage.getItem("byte_height_inches"),
    byte_weight: localStorage.getItem("byte_weight"),
    byte_goal: localStorage.getItem("byte_goal"),
  });
  const [errorState, setErrorState] = useState(null);

  const [quizStep, setQuizStep] = useState(0);
  useEffect(() => {
    console.log(values);
  }, [values]);
  const handleChange = (e) => {
    let updatedValue = {};
    const id = "byte_" + e.target.id;
    let val = e.target.value === "" ? null : e.target.value;
    updatedValue = { [id]: val };
    setValues((values) => ({
      ...values,
      ...updatedValue,
    }));
  };

  switch (quizStep) {
    case 0:
      return (
        <Button
          variant="soft"
          color="primary"
          bgColor="primary"
          onClick={() => {
            setQuizStep(quizStep + 1);
          }}
        >
          GET STARTED
        </Button>
      );
    case 1:
      return (
        <div className="w-[40rem]">
          <div className="flex flex-col gap-3">
            <div aria-label="name-question">
              <FormLabel sx={{ fontSize: "1.3em" }}>First Name</FormLabel>
              <Input
                id="name"
                defaultValue={values.name}
                onChange={(e) => {
                  handleChange(e);
                }}
              />
            </div>
            <div className="flex gap-8">
              <div className="flex gap-2">
                <div>
                  <FormLabel
                    aria-label="age-question"
                    sx={{ fontSize: "1.3em" }}
                  >
                    Age
                  </FormLabel>
                  <Input
                    id="age"
                    type="number"
                    aria-label="age-question"
                    defaultValue={values.age}
                    onChange={(e) => {
                      handleChange(e);
                    }}
                    slotProps={{
                      input: {
                        min: 0,
                        max: 116,
                        step: 1,
                      },
                    }}
                  />
                </div>
                <div>
                  <FormLabel sx={{ fontSize: "1.3em" }}>Weight</FormLabel>
                  <Input
                    id="weight"
                    type="number"
                    aria-label="weight-question"
                    defaultValue={values.weight}
                    onChange={(e) => {
                      handleChange(e);
                    }}
                    slotProps={{
                      input: {
                        min: 0,
                        max: 1000,
                        step: 1,
                      },
                    }}
                    endDecorator={<div>lbs</div>}
                  />
                </div>
              </div>
              <div className="flex gap-2">
                <div>
                  <FormLabel
                    aria-label="age-question"
                    sx={{ fontSize: "1.3em" }}
                  >
                    Height
                  </FormLabel>
                  <div className="flex">
                    <Input
                      id="height_feet"
                      type="number"
                      aria-label="height-feet-question"
                      onChange={(e) => {
                        handleChange(e);
                      }}
                      defaultValue={values.height_feet}
                      endDecorator={<div>ft</div>}
                      slotProps={{
                        input: {
                          min: 0,
                          max: 116,
                          step: 1,
                        },
                      }}
                    />
                    <div>
                      <Input
                        id="height_inches"
                        type="number"
                        aria-label="height-inches-question"
                        onChange={(e) => {
                          handleChange(e);
                        }}
                        defaultValue={values.height_inches}
                        slotProps={{
                          input: {
                            min: 0,
                            max: 1000,
                            step: 1,
                          },
                        }}
                        endDecorator={<div>in</div>}
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div aria-label="goal-question">
              <FormLabel sx={{ fontSize: "1.3em" }}>
                What are your goals?
              </FormLabel>
              <RadioGroup
                aria-labelledby="goal-label"
                defaultValue={null}
                size="lg"
              >
                <div className="flex justify-between text-center">
                  {["Lose Weight", "Maintain Weight", "Gain Weight"].map(
                    (value) => (
                      <Sheet
                        key={value}
                        sx={{
                          p: 2,
                          borderRadius: "md",
                          boxShadow: "sm",
                          width: "30%",
                        }}
                      >
                        <Radio
                          id="goal"
                          label={`${value}`}
                          aria-label={`select-${value}`}
                          overlay
                          disableIcon
                          value={value}
                          onClick={(e) => {
                            handleChange(e);
                          }}
                        />
                      </Sheet>
                    )
                  )}
                </div>
              </RadioGroup>
            </div>

            <div aria-label="activity-level-question">
              <FormLabel sx={{ fontSize: "1.3em" }}>
                What is your level of physical activity?
              </FormLabel>
              <RadioGroup
                aria-labelledby="goal-label"
                defaultValue={null}
                size="lg"
              >
                <div className="flex justify-between text-center">
                  {[
                    {
                      title: "Sedentary",
                      description: "Little or no exercise",
                    },
                    {
                      title: "Moderately Active",
                      description: "Moderate exercise 3-5 days a week",
                    },
                    {
                      title: "Very Active",
                      description: "Hard exercise 6-7 days a week",
                    },
                  ].map((value) => (
                    <Sheet
                      key={value}
                      sx={{
                        p: 2,
                        borderRadius: "md",
                        boxShadow: "sm",
                        width: "30%",
                      }}
                    >
                      <Radio
                        label={`${value.title}`}
                        aria-label={`select-${value.title}`}
                        overlay
                        disableIcon
                        value={value.title}
                        onClick={() => {
                          localStorage.setItem(
                            "goal",
                            value.title + " with " + value.description
                          );
                        }}
                      />
                      <Typography>{value.description}</Typography>
                    </Sheet>
                  ))}
                </div>
              </RadioGroup>
              <div id="error-message">{errorState}</div>
            </div>

            <div className="w-full flex justify-between">
              <Button
                variant="soft"
                aria-label="previous-page-button"
                onClick={() => {
                  setQuizStep(quizStep - 1);
                }}
              >
                Previous
              </Button>
              <Button
                variant="soft"
                aria-label="next-page-button"
                onClick={() => {
                  console.log(!Object.values(values).every((o) => o === null))
                  let isvalid =
                    !Object.values(values).every((o) => o === null) &&
                    !localStorage.getItem("byte_quizStatus") === null
                      ? true
                      : false;
                  console.log(hasEmptyValue);
                  if (!isvalid) {
                    setErrorState(
                      "Please fill out every question before continuing."
                    );
                  } else {
                    setQuizStep(quizStep + 1);
                  }

                  console.log(errorState);
                }}
              >
                Next
              </Button>
            </div>
          </div>
        </div>
      );
    case 2:
      <div></div>
    default:
      <div>error</div>;
  }
}

export default Form;
